import React from 'react';
import axios from 'axios';

function App() {
    const franchiseData = [];
    async function fetchData() {
        try {
          const res = await axios.get('http://localhost:3000/api/products');
          franchiseData.push(res.data.products);
        } catch (error) {
          console.error('Error fetching data:', error.message);
        }
      }

      fetchData();
      console.log(franchiseData);

      // 원하는 업종
      const targetIndustry = '원하는 업종';

      // 해당 업종에 해당하는 상호 출력
      const matchingFranchises = franchiseData
        .filter(data => data.업종 === targetIndustry)
        .map(data => data.상호);


    return(
